-- Table structure for table `tp_link`
--

DROP TABLE IF EXISTS `tp_link`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `tp_link` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '友情链接ID',
  `title` varchar(60) NOT NULL COMMENT '友情链接标题',
  `url` varchar(100) NOT NULL COMMENT '友情链接URL',
  `desc` varchar(255) NOT NULL COMMENT '友情链接描述',
